sql.where:
DIST_MONTH = #{distMonth}
AND LGCS_CODE = #{lgcsCode}
AND SUPP_CODE = #{suppCode}
AND PD_CODE = #{pdCode}
AND BRAND_TYPE = #{brandType}
AND DIST_TYPE = #{distType}

mapper.select:
SELECT
count(0)
FROM DS_SUPP_DIST_NUM
WHERE
DIST_MONTH = #{filterMap.distMonth}
AND LGCS_CODE = #{filterMap.lgcsCode}
AND SUPP_CODE = #{filterMap.suppCode}
AND PD_CODE = #{filterMap.pdCode}
AND BRAND_TYPE = #{filterMap.brandType}
AND DIST_TYPE = #{filterMap.distType}

SELECT A.SL_CODE, C.SL_PD_ID, C.PKG_CODE AS normsCode, C.OUT_GW AS normsKg, C.OUT_SIZE AS normsOutSize, C.OUT_MTS AS normsOutTexture, C.PKG_TEN AS normsTen, C.OUT_LENGTH AS normsLength, C.OUT_WIDTH AS normsWidth, C.OUT_HEIGHT AS normsHeight, C.OUT_VOLUME AS normsVolume, C.OUT_SPEC AS normsOut, C.OUT_NW as normsKg FROM sl_seller A INNER JOIN sl_product B ON B.SL_CODE = A.SL_CODE
AND B.PD_CLASSES_CODE = #{filterMap.classesCode}
AND B.MACHINING_CODE = #{filterMap.machiningCode}
AND B.PD_BREED_CODE = #{filterMap.breedCode}
AND B.PD_FEATURE_CODE = #{filterMap.featureCode}
AND B.WEIGHT_CODE = #{filterMap.weightCode}
AND B.DEL_FLG = 0 INNER JOIN sl_pd_pkg C ON C.SL_CODE = B.SL_CODE AND C.SL_PD_ID = B.SL_PD_ID AND C.DEL_FLG = 0 WHERE A.DEL_FLG = 0 AND A.SL_CODE = #{filterMap.slCode}

SELECT
B.NORMS_CODE AS normsCode,
B.NETWEIGHT_OUT AS normsKg,
B.NORMS_OUT AS normsOut
FROM
pd_standard A
INNER JOIN pd_norms_std B ON B.STANDARD_ID = A.STANDARD_ID
AND B.DEL_FLG = 0
WHERE
A.DEL_FLG = 0

    AND A.CLASSES_CODE = #{filterMap.classesCode}


    AND A.MACHINING_CODE = #{filterMap.machiningCode}


    AND A.BREED_CODE = #{filterMap.breedCode}


    AND A.FEATURE_CODE = #{filterMap.featureCode}


    AND A.WEIGHT_CODE = #{filterMap.weightCode}
