sql.where:
AND employee.DEL_FLG = 0

    AND BUYER_ID=#{filterMap.buyerId}


    AND EMPLOYEE_NAME like #{filterMap.employeeName}


    AND EMPLOYEE_TEL like #{filterMap.employeeTel}


    AND EMPLOYEE_QQ like #{filterMap.employeeQq}


    AND EMPLOYEE_WECHAT like #{filterMap.employeeWechat}

mapper.select:
select employee.ID AS id, BUYER_ID AS buyerId, IFNULL(EMPLOYEE_TYPE,'') AS employeeType, IFNULL(EMPLOYEE_NAME,'') AS employeeName, IFNULL(EMPLOYEE_TEL,'') AS employeeTel, IFNULL(EMPLOYEE_QQ,'') AS employeeQq, IFNULL(EMPLOYEE_WECHAT,'') AS employeeWechat, CASE WHEN BUS_CARD_FLG = '0' THEN '无' ELSE '有' END AS busCardFlgName, BUS_CARD_FLG AS busCardFlg, CASE WHEN CONTACT_PERSON = '0' THEN '否' ELSE '是'END AS contactPersonName, CONTACT_PERSON AS contactPerson, CASE WHEN PURCHASE = '0' THEN '否' ELSE '是'END AS purchaseName, PURCHASE AS purchase, CASE WHEN RECEIVE_PERSON = '0' THEN '否' ELSE '是'END AS receivePersonName, RECEIVE_PERSON AS receivePerson from by_buyer_employee employee
ORDER BY id,buyerId
LIMIT #{startPos},#{pageSize}

SELECT TER_MARKET_ID AS terMarketId, MARKET_NAME AS marketName, MARKET_LEVEL AS marketLevel, MARKET_LEVEL_NAME AS marketLevelName FROM by_market_terminal WHERE LGCS_AREA_CODE = #{filterMap.lgcsAreaCode} AND CITY_CODE = #{filterMap.cityCode}
AND DISTRICT_CODE = #{filterMap.districtCode}
AND DEL_FLG = 0

mapper.update:
 UPDATE
     by_buyer_employee
 SET DEL_FLG = 1
,UPD_ID = #{updId}
,UPD_TIME = #{updTime}
 WHERE
     ID = #{id}
     AND BUYER_ID = #{buyerId}

UPDATE
by_buyer_account
SET
TEL_NO = #{telNo},
ACCOUNT_NAME = #{accountName}
,ACCOUNT_PASS = #{accountPass}
,UPD_ID = #{updId}
,UPD_TIME = #{updTime}
WHERE BUYER_ID = #{buyerId}
AND  DEL_FLG = '0'

mapper.select:
SELECT COUNT(1) FROM by_buyer_account
WHERE (TEL_NO = #{telNo}
OR  ACCOUNT_NAME=#{accountName})
AND DEL_FLG = '0'
AND  BUYER_ID != #{buyerId};
