mapper.select:
SELECT IFNULL(A.PROD_EP_ID,'') AS epId, IFNULL(B.EP_NAME,'') AS manufactureName, IFNULL(B.LIC_ADDR,'') AS addressName, IFNULL(C.SL_TEL,'') AS telephone, IFNULL(D.CERT_ITEM_VALUE,'') AS qltCertificate, IFNULL(F.PROVINCE_NAME,'') AS provinceName, IFNULL(G.CITY_NAME,'') AS cityName, IFNULL(E.SL_CODE,'') AS sellerID FROM sl_product AS A LEFT JOIN sl_enterprise AS B ON A.PROD_EP_ID = B.EP_ID LEFT JOIN sl_seller AS E ON A.PROD_EP_ID = E.EP_ID LEFT JOIN sl_account AS C ON C.SL_ACCOUNT = E.SL_ACCOUNT LEFT JOIN sl_ep_cert_item AS D ON D.EP_ID = A.PROD_EP_ID AND D.CERT_ID = '5' AND D.CERT_ITEM_ID = '2' LEFT JOIN md_province AS F ON E.PROVINCE_CODE = F.PROVINCE_CODE LEFT JOIN md_city AS G ON G.CITY_CODE = E.CITY_CODE
AND A.PD_CLASSES_CODE=#{filterMap.paramData.classesCode}
AND A.MACHINING_CODE=#{filterMap.paramData.machiningCode}
AND A.PD_BREED_CODE=#{filterMap.paramData.breedCode}
AND A.PD_FEATURE_CODE=#{filterMap.paramData.featureCode}
AND A.WEIGHT_CODE=#{filterMap.paramData.weightCode}
AND C.FROM_FLG=#{filterMap.paramData.platform}
GROUP BY A.PROD_EP_ID
