mapper.select:
select A.QLT_STD_CLA_ID AS qltStdClaId ,A.QLT_STD_SUB_ID AS qltStdSubId ,B.STANDARD_ID AS standardId ,A.QLT_STD_ITEM_ID AS qltStdItemId ,A.QLT_STD_ITEM_NAME AS qltStdItemName ,B.QLT_STD_EXC_VAL AS qltStdExcVal ,B.QLT_STD_SUIT_VAL AS qltStdSuitVal ,B.QLT_STD_UNQUAL_VAL AS qltStdUnqualVal ,B.REMARK AS remark ,B.DEL_FLG AS delFlg from PD_QLT_STD_ITEM AS A LEFT JOIN PD_QLT_STD AS B ON A.QLT_STD_ITEM_ID = B.QLT_STD_ITEM_ID AND
B.STANDARD_ID =#{filterMap.standardId}
where A.QLT_STD_SUB_ID=#{filterMap.qltStdSubId}

mapper.update:
update PD_STANDARD set QLT_FLG=#{qltFlg},
UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
where STANDARD_ID=#{standardId}
