mapper.select:
SELECT
COUNT(1)
FROM PD_NORMS_STD AS A
WHERE
A.NORMS_SUTTLE=#{pdNorms.normsSuttle}
AND A.NORMS_ERROR=#{pdNorms.normsError}
AND A.NORMS_NUMBER=#{pdNorms.normsNumber}
AND A.NORMS_SIZE=#{pdNorms.normsSize}
AND A.NORMS_TEXTURE=#{pdNorms.normsTexture}
AND A.NORMS_OUT=#{pdNorms.normsOut}
AND A.NORMS_KG=#{pdNorms.normsKg}
AND A.NORMS_OUT_SIZE=#{pdNorms.normsOutSize}
AND A.NORMS_OUT_TEXTURE=#{pdNorms.normsOutTexture}
AND A.NETWEIGHT_INNER=#{pdNorms.netweightInner}
AND A.NETWEIGHT_OUT=#{pdNorms.netweightOut}
AND A.STANDARD_ID=#{pdNorms.standardId}
AND A.NORMS_LENGTH=#{pdNorms.normsLength}
AND A.NORMS_WIDTH=#{pdNorms.normsWidth}
AND A.NORMS_HEIGHT=#{pdNorms.normsHeight}
AND A.NORMS_VOLUME=#{pdNorms.normsVolume}

mapper.update:
UPDATE PD_NORMS_STD AS A SET A.NORMS_SUTTLE=#{normsSuttle}, A.NORMS_ERROR=#{normsError}, A.NORMS_NUMBER=#{normsNumber}, A.NORMS_SIZE=#{normsSize}, A.NORMS_TEXTURE=#{normsTexture}, A.NORMS_OUT=#{normsOut}, A.NORMS_KG=#{normsKg}, A.NORMS_OUT_SIZE=#{normsOutSize}, A.NORMS_OUT_TEXTURE=#{normsOutTexture}, A.NETWEIGHT_INNER=#{netweightInner}, A.NETWEIGHT_OUT=#{netweightOut}, A.NORMS_LENGTH=#{normsLength}, A.NORMS_WIDTH=#{normsWidth}, A.NORMS_HEIGHT=#{normsHeight}, A.NORMS_VOLUME=#{normsVolume},
A.UPD_TIME= #{updTime}
WHERE A.STANDARD_ID=#{standardId} AND A.NORMS_CODE= #{normsCode}

mapper.insert:
INSERT INTO PD_NORMS_STD ( STANDARD_ID, NORMS_CODE, NORMS_SUTTLE, NORMS_ERROR, NORMS_NUMBER, NORMS_SIZE, NORMS_TEXTURE, NORMS_OUT, NORMS_KG, NORMS_OUT_SIZE, NORMS_OUT_TEXTURE, NORMS_TEN, NORMS_LENGTH, NORMS_WIDTH, NORMS_HEIGHT, NORMS_VOLUME, NETWEIGHT_INNER, NETWEIGHT_OUT, DEL_FLG, CRT_ID, CRT_TIME, UPD_ID, UPD_TIME, ACT_ID, ACT_TIME, VER ) VALUES ( #{standardId}, #{normsCode}, #{normsSuttle}, #{normsError}, #{normsNumber}, #{normsSize}, #{normsTexture}, #{normsOut}, #{normsKg}, #{normsOutSize}, #{normsOutTexture}, #{normsTen}, #{normsLength}, #{normsWidth}, #{normsHeight}, #{normsVolume}, #{netweightInner}, #{netweightOut},
'0',
#{crtId}, #{crtTime}, #{updId}, #{updTime}, #{actId}, #{actTime},
0
);

mapper.update:
update PD_STANDARD set
NOR_FLG=#{norFlg}

,VER = VER+1

where STANDARD_ID=#{standardId}

AND  (DEL_FLG IS NULL OR DEL_FLG  = 0)

mapper.delete:
DELETE FROM PD_NORMS_STD
WHERE
STANDARD_ID=#{filterMap.standardId}
AND NORMS_CODE = #{filterMap.normsCode}
