mapper.select:
SELECT x.*, y.LGCS_ID AS lgcsId,
( CASE WHEN y.LGCS_ID IS NOT NULL THEN '1' ELSE '0' END ) AS checkFlag, ( CASE WHEN y.PD_MARKET_CODE IS NULL THEN '1' ELSE y.PD_MARKET_CODE END ) AS pdMarketCode FROM (SELECT a.*, h.GRADE_CODE AS gradeCode, h.GRADE_NAME AS gradeName FROM ( SELECT a.CLASSES_CODE AS classesCode, a.CLASSES_NAME AS classesName, b.MACHINING_CODE AS machiningCode, b.MACHINING_NAME AS machiningName, c.BREED_CODE AS breedCode, c.BREED_NAME AS breedName, d.FEATURE_CODE AS featureCode, d.FEATURE_NAME AS featureName, e.WEIGHT_CODE AS weightCode, e.WEIGHT_NAME AS weightName, #{filterMap.lgcsAreaCode} AS lgcsAreaCode, ifnull(g.LGCS_NAME, #{filterMap.areaName}) AS lgcsAreaName
FROM pd_classes a INNER JOIN pd_machining b ON b.CLASSES_CODE = a.CLASSES_CODE INNER JOIN pd_breed c ON c.CLASSES_CODE = a.CLASSES_CODE AND c.MACHINING_CODE = b.MACHINING_CODE INNER JOIN pd_feature d ON d.CLASSES_CODE = a.CLASSES_CODE AND d.MACHINING_CODE = b.MACHINING_CODE AND d.BREED_CODE = c.BREED_CODE INNER JOIN pd_weight e ON e.CLASSES_CODE = a.CLASSES_CODE AND e.MACHINING_CODE = b.MACHINING_CODE AND e.BREED_CODE = c.BREED_CODE AND e.FEATURE_CODE = d.FEATURE_CODE LEFT JOIN pd_logiarea g ON g.LGCS_CODE = #{filterMap.lgcsAreaCode}
GROUP BY a.CLASSES_CODE, b.MACHINING_CODE, c.BREED_CODE, d.FEATURE_CODE, e.WEIGHT_CODE ORDER BY a.CLASSES_CODE, b.MACHINING_CODE, c.BREED_CODE, d.FEATURE_CODE, e.WEIGHT_CODE
) a, pd_grade h) x LEFT JOIN pd_logiarea y ON y.CLASSES_CODE = x.classesCode AND y.MACHINING_CODE = x.machiningCode AND y.BREED_CODE = x.breedCode AND y.FEATURE_CODE = x.featureCode AND y.WEIGHT_CODE = x.weightCode AND y.GRADE_CODE = x.gradeCode AND y.LGCS_CODE = #{filterMap.lgcsAreaCode}
ORDER BY x.classesCode, x.machiningCode,x.breedCode, x.featureCode,x.weightCode,x.gradeCode
LIMIT #{startPos},#{pageSize}

SELECT x.*,y.LGCS_ID AS lgcsId, ifnull(y.LGCS_NAME, '') AS lgcsAreaName, ( CASE WHEN y.LGCS_ID IS NOT NULL THEN '1' ELSE '0' END ) AS checkFlag FROM (SELECT a.*, h.GRADE_CODE AS gradeCode, h.GRADE_NAME AS gradeName FROM ( SELECT a.CLASSES_CODE AS classesCode, a.CLASSES_NAME AS classesName, b.MACHINING_CODE AS machiningCode, b.MACHINING_NAME AS machiningName, c.BREED_CODE AS breedCode, c.BREED_NAME AS breedName, d.FEATURE_CODE AS featureCode, d.FEATURE_NAME AS featureName, e.WEIGHT_CODE AS weightCode, e.WEIGHT_NAME AS weightName, #{filterMap.lgcsAreaCode} AS lgcsAreaCode, g.PD_MARKET_CODE AS pdMarketCode FROM pd_classes a INNER JOIN pd_machining b ON b.CLASSES_CODE = a.CLASSES_CODE INNER JOIN pd_breed c ON c.CLASSES_CODE = a.CLASSES_CODE AND c.MACHINING_CODE = b.MACHINING_CODE INNER JOIN pd_feature d ON d.CLASSES_CODE = a.CLASSES_CODE AND d.MACHINING_CODE = b.MACHINING_CODE AND d.BREED_CODE = c.BREED_CODE INNER JOIN pd_weight e ON e.CLASSES_CODE = a.CLASSES_CODE AND e.MACHINING_CODE = b.MACHINING_CODE AND e.BREED_CODE = c.BREED_CODE AND e.FEATURE_CODE = d.FEATURE_CODE LEFT JOIN pd_logiarea g ON g.LGCS_CODE = #{filterMap.lgcsAreaCode} ORDER BY a.CLASSES_CODE, b.MACHINING_CODE, c.BREED_CODE, d.FEATURE_CODE, e.WEIGHT_CODE
) a, pd_grade h) x LEFT JOIN pd_logiarea y ON y.CLASSES_CODE = x.classesCode AND y.MACHINING_CODE = x.machiningCode AND y.BREED_CODE = x.breedCode AND y.FEATURE_CODE = x.featureCode AND y.WEIGHT_CODE = x.weightCode AND y.GRADE_CODE = x.gradeCode AND y.LGCS_CODE = #{filterMap.lgcsAreaCode}
ORDER BY x.classesCode, x.machiningCode,x.breedCode, x.featureCode,x.weightCode,x.gradeCode
LIMIT #{startPos},#{pageSize}

SELECT count(1) FROM (SELECT a.*, h.GRADE_CODE AS gradeCode, h.GRADE_NAME AS gradeName FROM ( SELECT a.CLASSES_CODE AS classesCode, a.CLASSES_NAME AS classesName, b.MACHINING_CODE AS machiningCode, b.MACHINING_NAME AS machiningName, c.BREED_CODE AS breedCode, c.BREED_NAME AS breedName, d.FEATURE_CODE AS featureCode, d.FEATURE_NAME AS featureName, e.WEIGHT_CODE AS weightCode, e.WEIGHT_NAME AS weightName, #{filterMap.lgcsAreaCode} AS lgcsAreaCode, ifnull(g.LGCS_NAME, #{filterMap.areaName}) AS lgcsAreaName
FROM pd_classes a INNER JOIN pd_machining b ON b.CLASSES_CODE = a.CLASSES_CODE INNER JOIN pd_breed c ON c.CLASSES_CODE = a.CLASSES_CODE AND c.MACHINING_CODE = b.MACHINING_CODE INNER JOIN pd_feature d ON d.CLASSES_CODE = a.CLASSES_CODE AND d.MACHINING_CODE = b.MACHINING_CODE AND d.BREED_CODE = c.BREED_CODE INNER JOIN pd_weight e ON e.CLASSES_CODE = a.CLASSES_CODE AND e.MACHINING_CODE = b.MACHINING_CODE AND e.BREED_CODE = c.BREED_CODE AND e.FEATURE_CODE = d.FEATURE_CODE LEFT JOIN pd_logiarea g ON g.LGCS_CODE = #{filterMap.lgcsAreaCode}
GROUP BY a.CLASSES_CODE, b.MACHINING_CODE, c.BREED_CODE, d.FEATURE_CODE, e.WEIGHT_CODE ORDER BY a.CLASSES_CODE, b.MACHINING_CODE, c.BREED_CODE, d.FEATURE_CODE, e.WEIGHT_CODE
) a, pd_grade h) x LEFT JOIN pd_logiarea y ON y.CLASSES_CODE = x.classesCode AND y.MACHINING_CODE = x.machiningCode AND y.BREED_CODE = x.breedCode AND y.FEATURE_CODE = x.featureCode AND y.WEIGHT_CODE = x.weightCode AND y.GRADE_CODE = x.gradeCode AND y.LGCS_CODE = #{filterMap.lgcsAreaCode}
ORDER BY x.classesCode, x.machiningCode,x.breedCode, x.featureCode,x.weightCode,x.gradeCode

mapper.insert:
INSERT INTO pd_logiarea(
LGCS_ID,
LGCS_CODE,
LGCS_NAME,
CLASSES_CODE,
MACHINING_CODE,
BREED_CODE,
FEATURE_CODE,
WEIGHT_CODE,
GRADE_CODE,
PD_MARKET_CODE,
DEL_FLG,
CRT_ID,
CRT_TIME,
UPD_ID,
UPD_TIME,
ACT_ID,
ACT_TIME,
VER
)
SELECT ifnull(max(LGCS_ID), 0) + 1
, #{lgcsAreaCode}
, #{lgcsAreaName}
, #{classesCode}
, #{machiningCode}
, #{breedCode}
, #{featureCode}
, #{weightCode}
, #{gradeCode}
, #{pdMarketCode}
, #{delFlg}
, #{crtId}
, #{crtTime}
, #{updId}
, #{updTime}
, #{actId}
, #{actTime}
, #{ver}
FROM pd_logiarea
