mapper.select:
select SCIENTIFIC_NAME AS scientificName ,LOCAL_NAME AS localName ,SALES_NAME AS salesName ,PLACE_ORIGIN AS placeOrigin ,PLACE_CURRENT AS placeCurrent ,SOURCE AS source ,CHILD_TYPE AS childType ,FEED_TYPE AS feedType ,FEED_PERIOD AS feedPeriod from pd_classestree_mat
AND CLASSESTREE_CODE=#{filterMap.classCode}
AND DEL_FLG=#{filterMap.delFlg}

     SELECT
	A.CLASSESTREE_CODE AS classestreeCode
    FROM
	pd_classestree AS A
	WHERE A.PARENT_CODE=#{filterMap.classCode}
	AND A.TREE_LEVEL=#{filterMap.treeLevel}
	AND A.DEL_FLG=#{filterMap.delFlg}

SELECT
IFNULL(A.TC_ONLINE_ID,'') AS tcOnlineId,
IFNULL(A.FEATURE_FLG,'') AS featureFlg,
IFNULL(A.FEATURE_NAME,'') AS featureName
FROM
pd_tc_market AS A


        AND A.CLASSES_CODE=#{filterMap.classesCode}


        AND A.MACHINING_CODE=#{filterMap.machiningCode}


        AND A.BREED_CODE=#{filterMap.breedCode}


        AND A.DEL_FLG=#{filterMap.delFlg}

mapper.update:
update pd_tc_market set DEL_FLG=#{filterMap.delFlg}

,UPD_ID = #{updId},
UPD_TIME=#{updTime}


,VER = VER+1

where TC_ONLINE_ID=#{filterMap.tcOnlineId}

   AND  (DEL_FLG IS NULL OR DEL_FLG  = 0)

update pd_tc_market set FEATURE_FLG=#{filterMap.featureFlg}

,UPD_ID = #{updId},
UPD_TIME=#{updTime}


,VER = VER+1

where TC_ONLINE_ID=#{filterMap.tcOnlineId}

AND  (DEL_FLG IS NULL OR DEL_FLG  = 0)

mapper.select:
SELECT
A.TC_PROVIDER_ID AS tcProviderId,
