mapper.select:
SELECT CONCAT(A.CLASSESTREE_CODE,2) AS attributeCode, A.LEVEL_CODE AS featureCode, A.LEVEL_NAME AS featureName, B.LEVEL_CODE AS normsCode, B.LEVEL_NAME AS normsOut, A.PARENT_CODE AS parentCode, CONCAT(A.LEVEL_CODE,B.LEVEL_CODE) AS pdNormCode FROM pd_classestree AS A INNER JOIN pd_classestree AS B ON A.CLASSESTREE_CODE = B.PARENT_CODE
AND A.PARENT_CODE=#{filterMap.parentCode}
AND A.TREE_LEVEL=#{filterMap.treeLevel}

SELECT
A.TC_FORBID_ID AS tcForbidId,
A.FEATURE_NAME AS featureName,
A.FEATURE_FLG AS featureFlg
FROM
pd_tc_forbid AS A


        AND A.CLASSES_CODE=#{filterMap.classesCode}


        AND A.MACHINING_CODE=#{filterMap.machiningCode}


        AND A.BREED_CODE=#{filterMap.breedCode}


        AND A.DEL_FLG=#{filterMap.delFlg}

mapper.update:
update pd_tc_forbid set DEL_FLG=#{filterMap.delFlg}

,UPD_ID = #{updId},
UPD_TIME=#{updTime}


,VER = VER+1

where TC_FORBID_ID = #{filterMap.tcForbidId}

 AND  (DEL_FLG IS NULL OR DEL_FLG  = 0)
