mapper.update:
        UPDATE PD_FED_STD SET
        FED_GOOD_VAL = #{fedGoodVal},
        FED_NORMAL_VAL = #{fedNormalVal},
        FED_BAD_VAL = #{fedBadVal},
        UPD_ID = #{updId},

        UPD_TIME = #{updTime}


		,VER = VER+1

        WHERE STANDARD_ID = #{standardId}
        AND  FED_STD_ITEM_ID = #{fedStdItemId}

		AND  DEL_FLG='0'

UPDATE PD_STANDARD SET FED_FLG = #{filterMap.flg}, UPD_ID = #{filterMap.updId},
UPD_ID = #{updId},
UPD_TIME = #{updTime}
,VER = VER+1
WHERE STANDARD_ID = #{filterMap.standardId}
AND DEL_FLG='0'

