mapper.select:
SELECT A.ORG_STD_ITEM_ID AS orgStdItemId ,B.ORG_STD_ITEM_NAME AS orgStdItemName ,A.ORG_GOOD_VAL AS orgGoodVal ,A.ORG_NORMAL_VAL AS orgNormalVal ,A.ORG_BAD_VAL AS orgBadVal ,C.RESULT_FLG AS resultFlg ,C.RESULT_INFO AS resultInfo FROM PD_ORG_STD AS A LEFT JOIN PD_ORG_STD_ITEM AS B ON A.ORG_STD_ITEM_ID = B.ORG_STD_ITEM_ID LEFT JOIN PD_ORG_SELLER_ONLINE AS C ON A.STANDARD_ID = C.STANDARD_ID AND A.ORG_STD_ITEM_ID = C.ORG_STD_ITEM_ID
AND C.SL_ID = #{filterMap.slId}
AND C.SL_PD_ID = #{filterMap.slPdId}
AND C.CHECK_DATE = #{filterMap.showDate}
AND C.SL_CODE_DIS = #{filterMap.slCodeDis}
A.STANDARD_ID = #{filterMap.standardId}
ORDER BY A.ORG_STD_ITEM_ID

SELECT A.FED_STD_ITEM_ID AS fedStdItemId ,B.FED_STD_ITEM_NAME AS fedStdItemName ,A.FED_GOOD_VAL AS fedGoodVal ,A.FED_NORMAL_VAL AS fedNormalVal ,A.FED_BAD_VAL AS fedBadVal ,C.RESULT_FLG AS resultFlg ,C.RESULT_INFO AS resultInfo FROM PD_FED_STD AS A LEFT JOIN PD_FED_STD_ITEM AS B ON A.FED_STD_ITEM_ID = B.FED_STD_ITEM_ID LEFT JOIN PD_FED_SELLER_ONLINE AS C ON A.STANDARD_ID = C.STANDARD_ID AND A.FED_STD_ITEM_ID = C.FED_STD_ITEM_ID
AND C.CHECK_DATE = #{filterMap.showDate}
AND C.SL_ID = #{filterMap.slId}
AND C.SL_PD_ID = #{filterMap.slPdId}
AND C.SL_CODE_DIS = #{filterMap.slCodeDis}
A.STANDARD_ID = #{filterMap.standardId}
ORDER BY A.FED_STD_ITEM_ID

SELECT
A.MCT_STD_ITEM_ID AS mctStdItemId
,B.MCT_STD_ITEM_NAME AS mctStdItemName
,A.MCT_OK_VAL AS mctOkVal
,A.MCT_NG_VAL AS mctNgVal
,C.RESULT_FLG AS resultFlg
,C.RESULT_INFO AS resultInfo
FROM PD_MCT_STD AS A
LEFT JOIN PD_MCT_STD_ITEM AS B ON A.MCT_STD_ITEM_ID = B.MCT_STD_ITEM_ID
LEFT JOIN PD_MCT_SELLER_ONLINE AS C ON A.STANDARD_ID = C.STANDARD_ID AND A.MCT_STD_ITEM_ID = C.MCT_STD_ITEM_ID

    AND C.CHECK_DATE = #{filterMap.showDate}


    AND C.SL_ID = #{filterMap.slId}


    AND C.SL_PD_ID = #{filterMap.slPdId}


    AND C.SL_CODE_DIS = #{filterMap.slCodeDis}



        A.STANDARD_ID = #{filterMap.standardId}


ORDER BY A.MCT_STD_ITEM_ID

SELECT A.STANDARD_ID AS standardId, A.TNC_STD_ITEM_ID AS tncStdItemId, A.TNC_STD_VAL1 AS tncStdVal1, A.TNC_STD_VAL2 AS tncStdVal2, A.TNC_STD_VAL3 AS tncStdVal3, A.REMARK AS remark, B.TNC_STD_ITEM_ID AS tncStdItemId, B.TNC_STD_ITEM_NAME AS tncStdItemName, B.LEVEL_ID AS levelId, B.PARENT_ID AS parentId, B.IS_CATALOG AS isCatalog ,C.RESULT_FLG AS resultFlg ,C.RESULT_INFO AS resultInfo FROM PD_TNC_STD_ITEM B LEFT JOIN PD_TNC_STD A ON A.TNC_STD_ITEM_ID=B.TNC_STD_ITEM_ID AND A.STANDARD_ID=#{filterMap.standardId} LEFT JOIN PD_TNC_SELLER_ONLINE AS C ON A.STANDARD_ID = C.STANDARD_ID AND A.TNC_STD_ITEM_ID = C.TNC_STD_ITEM_ID
AND C.CHECK_DATE = #{filterMap.showDate}
AND C.SL_ID = #{filterMap.slId}
AND C.SL_PD_ID = #{filterMap.slPdId}
AND C.SL_CODE_DIS = #{filterMap.slCodeDis}
WHERE B.LEVEL_ID=#{filterMap.levelId} AND B.PARENT_ID=#{filterMap.parentId}

SELECT
A.GNQ_STD_ITEM_ID AS gnqStdItemId
,A.GNQ_STD_ITEM_NAME AS gnqStdItemName
,B.GNQ_STD_ITEM_ID AS gnqStdItemId
,B.GNQ_OK_VAL AS gnqOkVal
,B.GNQ_NG_VAL AS gnqNgVal
,C.RESULT_FLG AS resultFlg
,C.RESULT_INFO AS resultInfo
FROM PD_GNQ_STD_ITEM AS A
LEFT JOIN PD_GNQ_STD AS B ON A.GNQ_STD_ITEM_ID = B.GNQ_STD_ITEM_ID AND B.STANDARD_ID = #{filterMap.standardId}
LEFT JOIN PD_GNQ_SELLER_ONLINE AS C ON B.STANDARD_ID = C.STANDARD_ID AND B.GNQ_STD_ITEM_ID = C.GNQ_STD_ITEM_ID

    AND C.CHECK_DATE = #{filterMap.showDate}


    AND C.SL_ID = #{filterMap.slId}


    AND C.SL_PD_ID = #{filterMap.slPdId}


    AND C.SL_CODE_DIS = #{filterMap.slCodeDis}

WHERE A.PARENT_ID = #{filterMap.parentId} AND A.LEVEL_ID = #{filterMap.levelId}
ORDER BY A.GNQ_STD_ITEM_ID

SELECT
A.SFT_STD_ITEM_NAME AS sftStdItemName
,A.SFT_STD_ITEM_ID AS sftStdItemId
,B.SFT_OK_VAL AS sftOkVal
,B.SFT_NG_VAL AS sftNgVal
,C.RESULT_FLG AS resultFlg
,C.RESULT_INFO AS resultInfo
FROM PD_SFT_STD_ITEM AS A
LEFT JOIN PD_SFT_STD AS B ON A.SFT_STD_ITEM_ID = B.SFT_STD_ITEM_ID AND B.STANDARD_ID = #{filterMap.standardId}
LEFT JOIN PD_SFT_SELLER_ONLINE AS C ON B.STANDARD_ID = C.STANDARD_ID AND B.SFT_STD_ITEM_ID = C.SFT_STD_ITEM_ID

    AND C.CHECK_DATE = #{filterMap.showDate}


    AND C.SL_ID = #{filterMap.slId}


    AND C.SL_PD_ID = #{filterMap.slPdId}


    AND C.SL_CODE_DIS = #{filterMap.slCodeDis}

WHERE A.PARENT_ID = #{filterMap.parentId} AND A.LEVEL_ID = #{filterMap.levelId}
ORDER BY A.SFT_STD_ITEM_ID

SELECT
A.TSP_STD_ITEM_NAME AS tspStdItemName
,A.TSP_STD_ITEM_ID AS tspStdItemId
,B.TSP_OK_VAL AS tspOkVal
,B.TSP_NG_VAL AS tspNgVal
,C.RESULT_FLG AS resultFlg
,C.RESULT_INFO AS resultInfo
FROM PD_TSP_STD_ITEM AS A
LEFT JOIN PD_TSP_STD AS B ON A.TSP_STD_ITEM_ID = B.TSP_STD_ITEM_ID AND B.STANDARD_ID = #{filterMap.standardId}
LEFT JOIN PD_TSP_SELLER_ONLINE AS C ON B.STANDARD_ID = C.STANDARD_ID AND B.TSP_STD_ITEM_ID = C.TSP_STD_ITEM_ID

    AND C.CHECK_DATE = #{filterMap.showDate}


    AND C.SL_ID = #{filterMap.slId}


    AND C.SL_PD_ID = #{filterMap.slPdId}


    AND C.SL_CODE_DIS = #{filterMap.slCodeDis}

WHERE A.PARENT_ID = #{filterMap.parentId} AND A.LEVEL_ID = #{filterMap.levelId}
ORDER BY A.TSP_STD_ITEM_ID

SELECT A.STANDARD_ID AS standardId, A.NORMS_CODE AS normsCode, A.NORMS_SUTTLE AS normsSuttle, A.NORMS_ERROR AS normsError, A.NORMS_NUMBER AS normsNumber, A.NORMS_SIZE AS normsSize, A.NORMS_TEXTURE AS normsTexture, A.NORMS_OUT AS normsOut, A.NORMS_KG AS normsKg, A.NORMS_OUT_SIZE AS normsOutSize, A.NORMS_OUT_TEXTURE AS normsOutTexture, B.NORMS_ID AS normsCodeOld, B.RESULT_INNERWEIGHT_FLG AS resultInnerweightFlg, B.RESULT_INNERERROR_FLG AS resultInnererrorFlg, B.RESULT_INNERCOUNT_FLG AS resultInnercountFlg, B.RESULT_INNERSIZE_FLG AS resultInnersizeFlg, B.RESULT_INNERMAT_FLG AS resultInnermatFlg, B.RESULT_OUTSPEC_FLG AS resultOutspecFlg, B.RESULT_OUTWEIGHT_FLG AS resultOutweightFlg, B.RESULT_OUTSIZE_FLG AS resultOutsizeFlg, B.RESULT_OUTMAT_FLG AS resultOutmatFlg FROM PD_NORMS_STD AS A LEFT JOIN PD_NORMS_SELLER_ONLINE AS B ON A.NORMS_CODE = B.NORMS_ID AND B.STANDARD_ID = (SELECT STANDARD_ID FROM PD_STANDARD WHERE CLASSESTREE_CODE=#{filterMap.classestreeCode})
AND B.CHECK_DATE = #{filterMap.showDate}
AND B.SL_ID = #{filterMap.slId}
AND B.SL_PD_ID = #{filterMap.slPdId}
AND B.SL_CODE_DIS = #{filterMap.slCodeDis}
WHERE A.STANDARD_ID = (SELECT STANDARD_ID FROM PD_STANDARD WHERE CLASSESTREE_CODE=#{filterMap.classestreeCode}) ORDER BY A.STANDARD_ID,A.NORMS_CODE

SELECT count(1)
FROM  PD_GRADE_SELLER_ONLINE
WHERE SL_ID = #{filterMap.slId}  AND SL_PD_ID = #{filterMap.slPdId} AND SL_CODE_DIS = #{filterMap.slCodeDis} AND STANDARD_ID = #{filterMap.standardId}
AND CHECK_DATE = #{filterMap.checkDate}

