mapper.select:
SELECT B.LEVEL_NAME AS classesName, A.LEVEL_NAME AS machiningName, C.LEVEL_NAME AS breedName, C.CLASSESTREE_CODE AS classestreeCode, G.SCIENTIFIC_NAME AS scientificName, G.LOCAL_NAME AS localName FROM pd_classestree AS A INNER JOIN ( SELECT CLASSESTREE_CODE, LEVEL_NAME FROM PD_CLASSESTREE WHERE CLASSESTREE_CODE = #{filterMap.classesCode} ) AS B ON B.CLASSESTREE_CODE = A.PARENT_CODE INNER JOIN pd_classestree C ON C.PARENT_CODE = A.CLASSESTREE_CODE
AND C.LEVEL_NAME LIKE #{filterMap.breedName}
INNER JOIN pd_classestree_mat G ON G.CLASSESTREE_CODE = C.CLASSESTREE_CODE
AND A.LEVEL_CODE = #{filterMap.machiningName}
order by B.CLASSESTREE_CODE,A.CLASSESTREE_CODE,C.CLASSESTREE_CODE
LIMIT #{startPos},#{pageSize};

SELECT COUNT(1) FROM pd_classestree AS A INNER JOIN ( SELECT CLASSESTREE_CODE, LEVEL_NAME FROM PD_CLASSESTREE WHERE CLASSESTREE_CODE = #{filterMap.classesCode} ) AS B ON B.CLASSESTREE_CODE = A.PARENT_CODE INNER JOIN pd_classestree C ON C.PARENT_CODE = A.CLASSESTREE_CODE
AND C.LEVEL_NAME LIKE #{filterMap.breedName}
INNER JOIN pd_classestree_mat G ON G.CLASSESTREE_CODE = C.CLASSESTREE_CODE
AND A.LEVEL_CODE = #{filterMap.machiningName}
