mapper.select:
SELECT A.LEVEL_NAME AS machiningName, C.LEVEL_NAME AS breedName, D.LEVEL_NAME as featureName, E.LEVEL_NAME as weightName, F.LEVEL_NAME as normsOut, CONCAT('A2') AS gradeName, CONCAT(D.CLASSESTREE_CODE,'2') AS attributeCode, CONCAT(D.CLASSESTREE_CODE,'2', right(F.CLASSESTREE_CODE,4)) AS classestreeCode, G.SCIENTIFIC_NAME AS scientificName, G.LOCAL_NAME AS localName FROM pd_classestree AS A INNER JOIN ( SELECT CLASSESTREE_CODE FROM PD_CLASSESTREE WHERE CLASSESTREE_CODE = #{filterMap.classesCode} ) AS B ON B.CLASSESTREE_CODE = A.PARENT_CODE INNER JOIN pd_classestree C ON C.PARENT_CODE = A.CLASSESTREE_CODE
AND C.LEVEL_NAME LIKE #{filterMap.breedName}
INNER JOIN pd_classestree_mat G ON G.CLASSESTREE_CODE = C.CLASSESTREE_CODE INNER JOIN pd_classestree D ON D.PARENT_CODE = C.CLASSESTREE_CODE INNER JOIN pd_classestree E ON E.PARENT_CODE = D.CLASSESTREE_CODE INNER JOIN pd_classestree F ON F.PARENT_CODE = E.CLASSESTREE_CODE
AND A.LEVEL_CODE = #{filterMap.machiningName}
order by B.CLASSESTREE_CODE,A.CLASSESTREE_CODE,C.CLASSESTREE_CODE,D.CLASSESTREE_CODE
LIMIT #{startPos},#{pageSize};

SELECT count(1) FROM pd_classestree AS A INNER JOIN ( SELECT CLASSESTREE_CODE, PARENT_CODE, LEVEL_NAME FROM PD_CLASSESTREE WHERE CLASSESTREE_CODE = #{filterMap.classesCode} ) AS B ON B.CLASSESTREE_CODE = A.PARENT_CODE INNER JOIN pd_classestree C ON C.PARENT_CODE = A.CLASSESTREE_CODE
AND C.LEVEL_NAME LIKE #{filterMap.breedName}
INNER JOIN pd_classestree_mat G ON G.CLASSESTREE_CODE = C.CLASSESTREE_CODE INNER JOIN pd_classestree D ON D.PARENT_CODE = C.CLASSESTREE_CODE INNER JOIN pd_classestree E ON E.PARENT_CODE = D.CLASSESTREE_CODE INNER JOIN pd_classestree F ON F.PARENT_CODE = E.CLASSESTREE_CODE
AND A.LEVEL_CODE = #{filterMap.machiningName}
