mapper.select:
SELECT IFNULL(B.LEVEL_NAME,'') AS machiningName, IFNULL(G.SCIENTIFIC_NAME,'') AS scientificName, IFNULL(G.LOCAL_NAME,'') AS localName, IFNULL(G.SALES_NAME,'') breedName, IFNULL(CONCAT(D.CLASSESTREE_CODE, 2),'') AS attributeCode, IFNULL(D.LEVEL_NAME,'') AS featureName, IFNULL('A2','') AS gradeName, IFNULL('2','') AS gradeCode, IFNULL(E.LEVEL_NAME,'') AS weightName, IFNULL(F.LEVEL_NAME,'') AS normsOut, (CASE WHEN IFNULL(SL.SL_MAIN_CLASS,'')='0' THEN '生产商' WHEN IFNULL(SL.SL_MAIN_CLASS,'')='1' THEN '自产型' WHEN IFNULL(SL.SL_MAIN_CLASS,'')='2' THEN '代理型' WHEN IFNULL(SL.SL_MAIN_CLASS,'')='3' THEN 'OEM型' ELSE '' END) AS slMainClassName, IFNULL(SL.SL_MAIN_CLASS,'') AS slMainClass, IFNULL(ET.LIC_ADDR,'') AS slLicAddr, IFNULL(ET.EP_NAME,'') AS slName, IFNULL(ET2.LIC_ADDR,'') AS sllfAddr, IFNULL(ET2.EP_NAME,'') AS sllfName, (CASE WHEN IFNULL(BD.BRAND_CLASS,'')='0' THEN '卖家独立品牌' WHEN IFNULL(BD.BRAND_CLASS,'')='1' THEN '神农先生联合' WHEN IFNULL(BD.BRAND_CLASS,'')='2' THEN '神农客联合' WHEN IFNULL(BD.BRAND_CLASS,'')='3' THEN '神农人家联合' ELSE '' END) AS brandClassName, IFNULL(BD.BRAND_CLASS,'')AS brandClass, IFNULL(BD.BRAND_NAME,'') AS brandName, IFNULL(BD.BRAND_ID,'') AS brandId, IFNULL(SL.SL_CODE_DIS,'')AS slCodeDis, IFNULL(SL2.SL_CODE_MANUFACTURE,'')AS slCodeManufacture, IFNULL(F.LEVEL_CODE,'')AS normsCode, IFNULL(PD.PD_CLASSES_CODE,'')AS pdClassesCode, IFNULL(PD.MACHINING_CODE,'')AS machiningCode, IFNULL(PD.PD_BREED_CODE,'')AS pdBreedCode, IFNULL(PD.PD_FEATURE_CODE,'')AS pdFeatureCode, IFNULL(PD.WEIGHT_CODE,'')AS weightCode FROM pd_classestree AS A INNER JOIN pd_classestree AS B ON A.CLASSESTREE_CODE = B.PARENT_CODE INNER JOIN pd_classestree AS C ON B.CLASSESTREE_CODE = C.PARENT_CODE INNER JOIN pd_classestree AS D ON C.CLASSESTREE_CODE = D.PARENT_CODE INNER JOIN pd_classestree AS E ON D.CLASSESTREE_CODE = E.PARENT_CODE INNER JOIN pd_classestree AS F ON E.CLASSESTREE_CODE = F.PARENT_CODE INNER JOIN pd_classestree_mat AS G ON C.CLASSESTREE_CODE = G.CLASSESTREE_CODE LEFT JOIN sl_product AS PD ON PD.PD_CLASSES_CODE = A.LEVEL_CODE AND PD.MACHINING_CODE = B.LEVEL_CODE AND PD.PD_BREED_CODE = C.LEVEL_CODE AND PD.PD_FEATURE_CODE = D.LEVEL_CODE AND PD.WEIGHT_CODE = E.LEVEL_CODE LEFT JOIN sl_pd_pkg PKG ON PKG.SL_PD_ID = PD.SL_PD_ID AND PKG.PKG_CODE=F.LEVEL_CODE LEFT JOIN sl_seller SL ON PD.SL_CODE = SL.SL_CODE LEFT JOIN sl_seller SL2 ON PD.PROD_EP_ID = SL2.EP_ID LEFT JOIN sl_enterprise ET ON ET.EP_ID = SL.EP_ID LEFT JOIN sl_enterprise ET2 ON ET2.EP_ID = PD.PROD_EP_ID LEFT JOIN sl_ep_brand BD ON BD.EP_ID = PD.BRAND_EP_ID AND BD.BRAND_ID = PD.BRAND_ID
ORDER BY B.CLASSESTREE_CODE, C.CLASSESTREE_CODE, D.CLASSESTREE_CODE, E.CLASSESTREE_CODE, F.CLASSESTREE_CODE, SL.SL_CODE
LIMIT #{startPos},#{pageSize};
