mapper.select:
SELECT
    SL_CODE AS slCode
from
    SL_PD_BRAND
WHERE
    SL_CODE = #{filterMap.slCode}
    AND BRAND_EP_ID =#{filterMap.brandEpId}
    AND BRAND_ID=#{filterMap.brandId}
    AND DEL_FLG = 0

SELECT EP_ID AS epId from SL_ENTERPRISE
 WHERE EP_ID = #{filterMap.epId}
 AND DEL_FLG = 0

SELECT EP_ID AS epId
FROM SL_EP_BRAND
WHERE EP_ID=#{filterMap.brandEpId}
 AND BRAND_ID=#{filterMap.brandId}
 AND DEL_FLG = 0

SELECT CERT_ID
FROM SL_MST_PD_CERT
WHERE
PD_CLASSES_CODE=#{filterMap.pdClassesCode}
AND MACHINING_CODE=#{filterMap.machiningCode}
AND DEL_FLG = 0

mapper.insert:
update sl_ep_dd set DD_NAME=#{ddName}, DD_EQUIPMENT=#{ddEquipment}, UPD_ID=#{updId}, UPD_TIME=#{updTime}, VER = VER + 1
and EP_ID=#{epId}
and DD_ID=#{ddId}

mapper.select:
select count(1) from sl_enterprise
DEL_FLG = 0
AND LIC_NO=#{filterMap.licNo}

select count(1) from sl_pd_classes
DEL_FLG = 0
AND SL_CODE=#{filterMap.slCode}

mapper.update:
update sl_ep_cert set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND EP_ID=#{epId}
AND CERT_ID=#{certId}

update sl_ep_honor set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND EP_ID=#{epId}
AND HONOR_ID=#{honorId}

update sl_ep_brand set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND EP_ID=#{epId}
AND BRAND_ID=#{brandId}

update sl_ep_brand_honor set
DEL_FLG= 1,
UPD_ID = #{updId},
UPD_TIME = #{updTime},
VER = VER + 1


		AND EP_ID=#{epId}


		AND BRAND_ID=#{brandId}


		AND HONOR_ID=#{honorId}

update sl_pd_brand set
DEL_FLG= 1,
UPD_ID = #{updId},
UPD_TIME = #{updTime},
VER = VER + 1


		AND SL_CODE=#{slCode}


		AND BRAND_ID=#{brandId}


		AND BRAND_EP_ID=#{brandEpId}

update sl_ep_workshop set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND EP_ID=#{epId}
AND WORKSHOP_ID=#{workShopId}

update sl_ep_agent_auth set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND SL_CODE=#{slCode}
AND PRODUCER_EP_ID=#{epId}

update sl_ep_oem_auth set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND SL_CODE=#{slCode}
AND PRODUCER_EP_ID=#{epId}

update sl_ep_manager set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND MEMBER_ID=#{memberId}
AND EP_ID=#{epId}

update sl_ec_team set DEL_FLG= 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND MEMBER_ID=#{memberId}
AND SL_CODE=#{slCode}

update sl_ep_dd set DEL_FLG = 1, UPD_ID = #{updId}, UPD_TIME = #{updTime}, VER = VER + 1
AND DD_ID=#{ddId}
AND EP_ID=#{epId}

mapper.select:
select CERT_NAME AS certName, DEL_FLG AS delFlg from sl_ep_cert
DEL_FLG = 0
AND CERT_ID=#{filterMap.certId}
AND EP_ID=#{filterMap.epId}

select
BRAND_NAME AS brandName,
DEL_FLG AS delFlg
from sl_pd_brand


		AND SL_CODE=#{filterMap.slCode}


		AND BRAND_ID=#{filterMap.brandId}


		AND BRAND_EP_ID=#{filterMap.brandEpId}

select SL_CODE AS brandName, PRODUCER_EP_ID AS producerEpId, AUTH_EP_NAME AS authEpName, DEL_FLG AS delFlg from sl_ep_agent_auth
AND SL_CODE=#{filterMap.slCode}
AND PRODUCER_EP_ID=#{filterMap.epId}

select SL_CODE AS brandName, PRODUCER_EP_ID AS producerEpId, AUTH_EP_NAME AS authEpName, DEL_FLG AS delFlg from sl_ep_oem_auth
AND SL_CODE=#{filterMap.slCode}
AND PRODUCER_EP_ID=#{filterMap.epId}
